mapper.insert:
INSERT INTO message_log (
favorite_id, message_content, enable_flag, create_user, create_time, update_user, update_time
) VALUES (
#{favoriteId}, #{messageContent}, #{enableFlag}, #{createUser}, #{createTime}, #{updateUser}, #{updateTime}
)

mapper.update:
UPDATE message_log


        message_content = #{messageContent},


        enable_flag = #{enableFlag},

    update_user = #{updateUser},
    update_time = #{updateTime}

WHERE id = #{id}
